import { createSubject, observableOf } from "../helpers";
import { isObservable } from "rxjs";
import { scan, filter, delay, first, startWith, concatMap, tap, takeUntil, } from "rxjs/operators";
export var DefaultStoreAction;
(function (DefaultStoreAction) {
    DefaultStoreAction["ASYNC_UI_ACTION"] = "[ASYNC_REQUEST]";
    DefaultStoreAction["ERROR_ACTION"] = "[REQUEST_ERROR]";
    DefaultStoreAction["INITIALIZE_STORE_STATE"] = "[RESET_STORE_STATE]";
})(DefaultStoreAction || (DefaultStoreAction = {}));
const instanceOfInjectableStore = (value) => "getInjectedStore" in value;
export class InjectableStore {
    get state$() {
        return this.getInjectedStore().connect();
    }
}
export class DrewlabsFluxStore {
    /**
     * @description EntityState instance initializer
     */
    constructor(reducer, initial) {
        this._destroy$ = createSubject();
        this._store$ = createSubject(1);
        // tslint:disable-next-line: variable-name
        this.state$ = this._store$.asObservable();
        // tslint:disable-next-line: variable-name
        this._actions$ = createSubject();
        // @internal
        this.subscribeToActions = (reducer, initial) => {
            this._actions$
                .pipe(takeUntil(this._destroy$), concatMap((action) => isObservable(action)
                ? action
                : observableOf(action)), filter((state) => typeof state !== "undefined" && state !== null), startWith(initial), scan(reducer), tap((state) => this._store$.next(state)))
                .subscribe();
        };
        this.bindActionCreator = (handler) => (...args) => {
            const action = handler.call(null, ...args);
            this._actions$.next(action);
            if (isObservable(action.payload)) {
                // Simulate a wait before calling the next method
                observableOf([action.payload])
                    .pipe(first(), delay(10))
                    .subscribe((state) => {
                    this._actions$.next(state[0]);
                });
            }
            return action;
        };
        /**
         * @description Connect to the store data stream
         */
        this.connect = () => this.state$;
        /**
         * Provides a destruction mechanism to the store
         *
         * @returns
         */
        this.destroy = () => (() => {
            this._store$.complete();
            this._destroy$.next();
        })();
        this.subscribeToActions(reducer, initial);
    }
}
// tslint:disable-next-line: typedef
export function createAction(rxStore, actionCreator) {
    if (instanceOfInjectableStore(rxStore)) {
        return rxStore
            .getInjectedStore()
            .bindActionCreator(actionCreator);
    }
    return rxStore.bindActionCreator(actionCreator);
}
export const createStore = (reducer, initialState) => {
    return new DrewlabsFluxStore(reducer, initialState);
};
export const onErrorAction = (store) => createAction(store, (payload) => ({ type: DefaultStoreAction.ERROR_ACTION, payload }));
export const onInitStoreStateAction = (store) => createAction(store, (payload = {}) => ({ type: DefaultStoreAction.INITIALIZE_STORE_STATE, payload }));
export const asyncUIAction = (store) => createAction(store, (payload = {}) => ({ type: DefaultStoreAction.ASYNC_UI_ACTION, payload }));
